import {Component, HostListener, OnDestroy, OnInit, ViewEncapsulation} from '@angular/core';
import {ActivatedRoute, Router} from '@angular/router';
import {Subject} from 'rxjs';
import {takeUntil} from 'rxjs/operators';
import {FuseMediaWatcherService} from '@fuse/services/media-watcher';
import {FuseNavigationService, FuseVerticalNavigationComponent} from '@fuse/components/navigation';
import {Navigation} from 'app/core/navigation/navigation.types';
import {NavigationService} from 'app/core/navigation/navigation.service';
import {UserUtilsService} from "../../../../shared/services/users_utils.service";
import {SessionExpireService} from 'app/shared/services/session-expire.service';
import {environment} from "../../../../../environments/environment";

@Component({
    selector: 'classic-layout',
    templateUrl: './classic.component.html',
    styleUrls: ['./classic.component.scss'],
    encapsulation: ViewEncapsulation.None
})
export class ClassicLayoutComponent implements OnInit, OnDestroy {
    user: any;
    isScreenSmall: boolean;
    userInfo;
    time: any;
    navigation: Navigation;
    sessionTime: any;
    popup: any = false;

    showMapViolate = false;
    showMapReq = false;
    showNotification = false;
    role = '';
    private _unsubscribeAll: Subject<any> = new Subject<any>();

    /**
     * Constructor
     */
    constructor(
        private _activatedRoute: ActivatedRoute,
        private _router: Router,
        private _navigationService: NavigationService,
        private userUtils: UserUtilsService,
        private _fuseMediaWatcherService: FuseMediaWatcherService,
        private _fuseNavigationService: FuseNavigationService,
        private _sessionExpireService: SessionExpireService,
    ) {

    }

    get currentYear(): number {
        return new Date().getFullYear();
    }

    ngOnInit(): void {


        this.user = JSON.parse(localStorage.getItem('ZTBLUser')).User;

        this.userInfo = this.userUtils.getUserDetails().User.userGroup;
        this.userInfo.forEach((single_group) => {
            this.role = this.role + environment[single_group.ProfileID];
        })
        this._navigationService.navigation$
            .pipe(takeUntil(this._unsubscribeAll))
            .subscribe((navigation: Navigation) => {
                this.navigation = navigation;
            });

        this._fuseMediaWatcherService.onMediaChange$
            .pipe(takeUntil(this._unsubscribeAll))
            .subscribe(({matchingAliases}) => {
                this.isScreenSmall = !matchingAliases.includes('md');
            });
        this.SessionStart();


    }

    IsIconVisable(url) {
        var ismatch = false
        var user = localStorage.getItem("ZTBLUser")
        if (user) {
            var userdate = JSON.parse(user);
            userdate.MenuBar.forEach(x => {
                var childURl = x?.children?.find(y => y.link?.includes(url))
                if (childURl) {
                    ismatch = true;
                } else {

                }

            });
            return ismatch;
        }
    }

    ngOnDestroy(): void {
        // this._sessionExpireService.timerUnSubject();
        // this._unsubscribeAll.next();
        // this._unsubscribeAll.complete();
    }

    SessionStart() {
        this.setSessionTime();
        this._sessionExpireService.count.subscribe(c => {
            if (c == 0) {
                this.Logout();
                return;
            }
            if (c < 60) {
                this.popup = true;
                this.beep()
            }
            if (c > 60) {
                this.popup = false;
            }

            this.time = this.secondsToHms(c); //c.toString();
        });
    }

    toggleNavigation(name: string): void {
        const navigation = this._fuseNavigationService.getComponent<FuseVerticalNavigationComponent>(name);
        if (navigation) {
            navigation.toggle();
        }
    }

    @HostListener('window:keydown')
    @HostListener('window:mousedown')
    // @HostListener('mousemove')
    checkUserActivity() {
        this.setSessionTime();
    }

    setSessionTime() {
        this.sessionTime = JSON.parse(localStorage.getItem("ZTBLUser"))?.SessionExpiryTime;
        this._sessionExpireService.timer(Number(this.sessionTime));
        //this._sessionExpireService.timer(1);
    }

    RefreshToken() {
        this.setSessionTime();
        this.popup = false;
        //this.hidePopup();
    }

    Logout() {
        this.hidePopup();
        this._router.navigate(['/sign-out']);
    }

    beep() {
        //var snd = new Audio("data:audio/wav;base64,");
        // snd.play();
    }

    hidePopup() {

        this.popup = false;
    }

    secondsToHms(d) {
        d = Number(d);
        var h = Math.floor(d / 3600);
        var m = Math.floor(d % 3600 / 60);
        var s = Math.floor(d % 3600 % 60);

        var hDisplay = h > 0 ? h + (h == 1 ? " hour, " : " hours, ") : "";
        var mDisplay = m > 0 ? m + (m == 1 ? " minute, " : " minutes, ") : "";
        var sDisplay = s > 0 ? s + (s == 1 ? " second" : " seconds") : "";
        return hDisplay + mDisplay + sDisplay;
    }

    getRoleName() {
        console.log(this.userInfo);
        // return this.userInfo;
    }
}
